class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)
        ct = Counter()
        for i in range(n//2):
            diff = abs(nums[i] - nums[n-i-1])
            ct[diff] += 1

        res = inf
        test = list(ct.keys())
        test.sort(key=lambda k: ct[k], reverse=True)
        for x in test:     
            tmp = 0
            if n//2 - ct[x] >= res:
                break
            for i in range(n//2):
                j = n - i - 1
                if abs(nums[i] - nums[j]) != x:
                    small, big = min(nums[i], nums[j]), max(nums[i], nums[j])
                    if big - x < 0 and small + x > k:
                        tmp += 2
                    else:
                        tmp += 1

            res = min(res, tmp) 

        return res
