const int N = 1E5 + 10;
class Solution {
    int n, a[N], pre[N];
public:
    int minChanges(vector<int>& nums, int k) {
        n = nums.size();
        for (int i = 1; i <= n; i++) {
            a[i] = nums[i - 1];
        }
        int cnt0 = 0;
        for (int i = 1; i <= n / 2; i++) {
            int x = a[i], y = a[n + 1 - i];
            int t = abs(x - y);
            if (t) {
                cnt0++;
                pre[1]++;
                pre[t]--;
                pre[t + 1]++;
                pre[max(abs(k - min(x, y)), abs(max(x, y))) + 1]--;
                if (max(abs(k - min(x, y)), abs(max(x, y))) + 1 < N) {
                    pre[max(abs(k - min(x, y)), abs(max(x, y))) + 1] += 2;
                }
            } else {
                pre[1]++;
                pre[max(abs(k - x), x) + 1]--;
                if (max(abs(k - x), x) + 1 < N) {
                    pre[max(abs(k - x), x) + 1] += 2;
                }
            }
        }
        for (int i = 1; i < N; i++) {
            pre[i] += pre[i - 1];
        }
        int ans = cnt0;
        for (int i = 1; i < N; i++) {
            ans = min(ans, pre[i]);
        }
        return ans;
    }
};
