class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        map<int,int> mp;
        vector<int> v(k+1,0);
        for(int i=0;i<n-i-1;i++){
            int a = min(nums[i], nums[n-1-i]);
            int b = max(nums[i], nums[n-1-i]);
            mp[b-a]++;
            int issue = max(k-a+1,b+1);
            if(issue <= k)v[issue]++;
        }
        int an = n;
        for(int i=1;i<=k;i++){
            v[i] += v[i-1];
        }
        for(int i=0;i<=k;i++){
            int pairs = n/2 - mp[i];
            an =min(an, pairs + v[i]);
        }
        return an;
    }
};
