class Solution {
public:
    // vector<int>v;
    int diff[100005];
    int r[100005];
    vector<pair<int,int>>v;
    unordered_map<int,int>mp;
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        int nn=n/2;
        for(int i=0;i<nn;i++)
        {
            diff[i]=abs(nums[i]-nums[n-i-1]);
            r[i]=max(max(nums[i],k-nums[i]),max(nums[n-i-1],k-nums[n-i-1]));
            v.push_back({r[i],diff[i]});
            mp[diff[i]]++;
        }
        sort(v.begin(),v.end());
        int minn=1e9;
        for(int x=0;x<=k;x++)
        {
            int s1=mp[x];
            int c=lower_bound(v.begin(),v.end(),pair(x,0))-v.begin();
            int d1=nn-c;
            int d2=c;
            minn=min(2*d2+d1-s1,minn);
        }
        return minn;
    }
};
