class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = size(nums);
        map<int, int> diffs;
        for (int i = 0; i < n/2; i++) {
            diffs[abs(nums[i]-nums[n-i-1])]++;
        }

        int mx = -1;
        int mx2 = -1;
        int mxltk = -1;
        for (auto i: diffs) {
            if (diffs[mx] < i.second) mx2 = mx, mx = i.first;
            else if (diffs[mx2] < i.second) mx2 = i.first;
            if (i.first <= (k+1)/2 && diffs[mxltk] < i.second) mxltk = i.first;
        }

        int ans = 0;
        if (mx != -1) {
            int num = 0;
            for (int i = 0; i < n/2; i++) {
                if (abs(nums[i]-nums[n-i-1]) == mx) continue;
                if (k-nums[i] >= mx || nums[i] >= mx || k-nums[n-i-1] >= mx || nums[n-i-1] >= mx) {
                    num++;
                } else 
                    num += 2;
            }
            ans = num;
        }
        if (mx2 != -1) {
            int num = 0;
            for (int i = 0; i < n/2; i++) {
                if (abs(nums[i]-nums[n-i-1]) == mx2) continue;
                if (k-nums[i] >= mx2 || nums[i] >= mx2 || k-nums[n-i-1] >= mx2 || nums[n-i-1] >= mx2) {
                    num++;
                } else 
                    num += 2;
            }
            ans = min(ans, num);
        }
        if (mxltk != -1) {
            int num = 0;
            for (int i = 0; i < n/2; i++) {
                if (abs(nums[i]-nums[n-i-1]) == mxltk) continue;
                if (k-nums[i] >= mxltk || nums[i] >= mxltk || k-nums[n-i-1] >= mxltk || nums[n-i-1] >= mxltk) {
                    num++;
                } else 
                    num += 2;
            }
            ans = min(ans, num);
        }
        return ans;
    }
};
