class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> occ(k + 1, 0), minVal(k + 1, 0);
        int n = nums.size();
        for(int i = 0; i < n/2; ++i) {
            occ[abs(nums[i] - nums[n - i - 1])]++;
            int min_val = max(max(nums[i], nums[n - i - 1]), k - min(nums[i], nums[n - i - 1]));
            minVal[min_val]++;
        }
        for(int i = k - 1; i >= 0; --i) {
            minVal[i] += minVal[i + 1];
        }
        int ans = 1e9, m = n / 2;
        for(int i = 0; i <= k; ++i) {
            ans = min(ans, m - occ[i] + m - minVal[i]);
        }

        return ans;
    }
};
