class Solution {
public:
    int minChanges(vector<int>& nums, int k)
    {
        int n = nums.size();
        map<int,int> mpp;

        for(int i = 0; i < n/2; i++)
        {
            int diff = abs(nums[i] - nums[n-i-1]);
            mpp[diff]++;
        }

        vector<pair<int,int>> v;
        for(auto &[diff, ct]: mpp)
        {
            v.push_back({ct, diff});
        }

        sort(v.begin(), v.end(), [](const pair<int,int> &a, const pair<int,int> &b)
        {
            if(a.first != b.first)
            {
                return a.first > b.first;
            }
            else
            {
                return a.second < b.second;
            }
        });

        // for(auto &[ct, diff]: v)
        // {
        //     cout<<diff<<" "<<ct<<endl;
        // }

        int X = v[0].second;

        int ans = 0;
        for(int i = 0; i < n/2; i++)
        {
            if(abs(nums[i] - nums[n-i-1]) == X)
            {
                continue;
            }
            else
            {
                if(abs(nums[i] - nums[n-i-1]) > X)
                {
                    ans++;
                }
                else
                {
                    if(X - abs(nums[i] - nums[n-i-1]) + max(nums[i], nums[n-i-1]) <= k)
                    {
                        ans++;
                    }
                    else if(min(nums[i], nums[n-i-1]) - (X - abs(nums[i] - nums[n-i-1])) >= 0)
                    {
                        ans++;
                    }
                    else
                    {
                        ans = ans + 2;
                    }
                }
            }
        }


        if(v.size() > 1)
        {
            int Y = v[1].second;
            int ans1 = 0;
            for(int i = 0; i < n/2; i++)
            {
                if(abs(nums[i] - nums[n-i-1]) == Y)
                {
                    continue;
                }
                else
                {
                    if(abs(nums[i] - nums[n-i-1]) > Y)
                    {
                        ans1++;
                    }
                    else
                    {
                        if(Y - abs(nums[i] - nums[n-i-1]) + max(nums[i], nums[n-i-1]) <= k)
                        {
                            ans1++;
                        }
                        else if(min(nums[i], nums[n-i-1]) - (Y - abs(nums[i] - nums[n-i-1])) >= 0)
                        {
                            ans1++;
                        }
                        else
                        {
                            ans1 = ans1 + 2;
                        }
                    }
                }
            }

            ans = min(ans, ans1);

        }

        return ans;
    }
};
