class Solution {
public:
    int minChanges(vector<int>& arr, int k) {
        int ans=1e9,n=arr.size();
        map<int,int>mp;
        vector<int>v(k+2,0);
        for(int i=0;i<n/2;i++){
            mp[abs(arr[i]-arr[n-i-1])]++;
            v[0]+=1;
            int p=max(abs(arr[i]),abs(arr[i]-k));
            int q=max(arr[n-1-i],abs(arr[n-1-i]-k));
            v[max(p,q) + 1]-=1;
        }
        for(int i=1;i<k+2;i++){
            v[i]+=v[i-1];
        }
        // for(int i=0;i<n+1;i++)cout<<v[i]<<" ";cout<<endl;
        for(int i=0;i<k+1;i++){
            // cout<<n-mp[i]+n-v[i]<<" ";
            ans=min(ans,(n/2)-mp[i]+(n/2)-v[i]);
        }
        return ans;
    }
};
