class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        unordered_map<int,int> m;
        vector<int> v;
        for(int i=0;i<n/2;i++){
            int a=nums[i];
            int b=nums[n-i-1];
            int x=min(a,b);
            int y=max(a,b);
            int z=k-x;
            int p=y-0;
            z=max(z,p);
            v.push_back(z);
            int ab=abs(a-b);
            m[ab]++;


        }
        sort(v.begin(),v.end());
        int ans=INT_MAX;
        for(auto i:m){
            int x=i.first;
            int y=i.second;
            int z=n/2-y;
            auto l=lower_bound(v.begin(),v.end(),x);
            int p=l-v.begin();
            int q=z+p;
            ans=min(ans,q);


        }
        return ans;


    }
};
