class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        vector<int>maxDiff(n/2,0);
        map<int,int>mp;
        for(int i=0;i<n/2;i++){
            int diff=abs(nums[i]-nums[n-i-1]);
            mp[diff]++;
            int mn=min(nums[i],nums[n-i-1]);
            int mx=max(nums[i],nums[n-i-1]);
            mx=k-mx;
            diff+=max(mn,mx);
            maxDiff[i]=diff;
        }
        sort(maxDiff.begin(),maxDiff.end());
        int ans=n/2;
        for(int i=0;i<=k;i++){
            int lb=lower_bound(maxDiff.begin(),maxDiff.end(),i)-maxDiff.begin();
            int both=lb;
            int single=(n/2)-both;
            int total=(both*2)+single-mp[i];
            ans=min(ans,total);
        }
        return ans;
    }
};
