class Solution:
    def minChanges(self, a: List[int], k: int) -> int:
        n = len(a)
        from collections import defaultdict
        c = defaultdict(int)
        b = [0 for _ in range(k + 1)]
        for i in range(n):
            if i > n - i - 1:
                continue
            u, v = a[i], a[n - i - 1]
            if u > v:
                u, v = v, u
            b[max(k - u, v)] += 1
            c[v - u] += 1
        for i in range(1, k + 1):
            b[i] += b[i - 1]
        ans = 999999999
        # print(b)
        # print(c)
        m = n // 2
        for x in range(k + 1):
            if x == 0:
                cur = m
            else:
                cur = b[x - 1] * 2 + (m - b[x - 1]) * 1
            cur -= c[x]
            ans = min(cur, ans)
        return ans
