typedef long long ll;

#define pb push_back
#define sz(x) (int)x.size()
#define all(x) begin(x),end(x)
#define lb(x,y) lower_bound(all(x),y)-begin(x)
class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int N = (int)nums.size();
        map<int, int> freq;
        priority_queue<int> pq;
        vector<int> v = {0};
        for (int i = 0; i < N - 1 - i; i++) {
            int j = N - 1 - i;
            int a = abs(nums[i] - nums[j]);
            int m = max(abs(nums[i] - k), nums[i]);
            m = max(m, max(abs(nums[j] - k), nums[j]));
            freq[a]++;
            pq.push(m);
            v.pb(a);
        }
        sort(all(v));
        reverse(all(v));
        int cur = 0;
        int res = INT_MAX;
        for (int i : v) {
            while (!pq.empty() && pq.top() >= i) {
                cur++;
                pq.pop();
            }
            int one = cur - freq[i], two = N / 2 - one - freq[i];
            res = min(res, one + two * 2);
        }
        return res;
    }
};
