class Solution {
    List<List<Integer>> pairs;

    public int minChanges(int[] arr, int k) {
        int n = arr.length;
        pairs = new ArrayList<>();

        // Create pairs of absolute differences and necessary changes
        for (int i = 0; i < n / 2; i++) {
            pairs.add(new ArrayList<>());
            int diff = Math.abs(arr[i] - arr[n - i - 1]);
            pairs.get(i).add(diff);
            int max1 = arr[i] < arr[n - i - 1] ? arr[i] : arr[n - i - 1];
            int max2 = arr[i] > arr[n - i - 1] ? arr[i] : arr[n - i - 1];
            pairs.get(i).add(Math.max(k - max1, max2 - 0));
        }

        // Sort pairs based on the absolute difference
        Collections.sort(pairs, (x, y) -> x.get(0) - y.get(0));

        // Compute the minimum changes required
        return computeMinimumChanges(arr, k);
    }

    private int computeMinimumChanges(int[] arr, int k) {
        int n = arr.length;
        PriorityQueue<Integer> pq = new PriorityQueue<>();
        int i = 0, cnt = 0;
        int minChanges = n;

        while (i < n / 2) {
            // Remove elements from the priority queue that are no longer relevant
            while (!pq.isEmpty() && pq.peek() < pairs.get(i).get(0)) {
                cnt++;
                pq.poll();
            }

            // Calculate the current value of changes
            int currentValue = 2 * cnt + pq.size();
            int currentDiff = pairs.get(i).get(0);

            // Add elements from the current pair to the priority queue
            while (i < n / 2 && pairs.get(i).get(0) == currentDiff) {
                pq.add(pairs.get(i).get(1));
                i++;
            }

            // Calculate the final value for this iteration
            currentValue = currentValue - i + (n / 2);
            minChanges = Math.min(minChanges, currentValue);
        }

        return minChanges;
    }
}
