class Solution {
private:
    vector<int> argsort(vector<int> & vals) {
        int n = vals.size();
        vector<int> ans(n, 0);
        for(int i = 0; i < n; i ++ ) {
            ans[i] = i;
        }
        sort(ans.begin(), ans.end(), [&vals](int a, int b) {return vals[a] > vals[b];});
        return ans;
    }
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> cnt(k + 1, 0);
        int n = nums.size();
        int m = n / 2;
        for(int i = 0; i < m; i ++ ) {
            cnt[abs(nums[i] - nums[n - i - 1])] += 1;
        }
        /*
        int maxidx = 0;
        for(int i = 1; i <= k; i ++ ) {
            if(cnt[i] > cnt[maxidx]) {
                maxidx = i;
            }
        }
        // cout << "maxidx" << maxidx << endl;
        for(int i = 0; i <= k; i ++ ) {
            cout << i << " " << rank[i] << " " << cnt[rank[i]] << endl;
        }
        */
        vector<int> rank = argsort(cnt);
        int res = n;
        for(int idx = 0; idx <= k; idx ++ ) {
            int maxidx = rank[idx];
            int ans = 0;
            if(res < m - cnt[maxidx]) {
                break;
            }
            for(int i = 0; i < m; i ++ ) {
                if(abs(nums[i] - nums[n-i-1]) != maxidx) {
                    if(nums[i] > nums[n - i - 1]) {
                        swap(nums[i], nums[n - i - 1]);
                    }
                    if(
                        (nums[n - i - 1] - maxidx >= 0) || 
                        (maxidx + nums[n - i - 1] <= k) || 
                        (nums[i] - maxidx >= 0) || 
                        (maxidx + nums[i] <= k)
                    ) {
                        ans += 1;
                    }
                    else{
                        ans += 2;
                    }
                }
            }
            res = min(res, ans);
        }
        return res;
    }
};
