class Solution {
 public:
  int minChanges(vector<int>& nums, int k) {
    int n = nums.size();
    vector<int> cnt(k + 1, 0);
    vector<int> mx(k + 1, 0);
    for (int i = 0; i < n / 2; ++i) {
      int a = nums[i];
      int b = nums[n - i - 1];
      cnt[abs(a - b)]++;
      a = min(a, k - a);
      b = min(b, k - b);
      mx[min(a, b)]++;
    }
    // for (auto c : mx) {
    //   cout << c << ' ';
    // }
    // cout << endl;
    for (int i = 1; i <= k; ++i) {
      mx[i] += mx[i - 1];
    }
    int ret = std::numeric_limits<int>::max();
    for (int i = k; i >= 0; --i) {
      // cout << i << ": " << mx[i] + 2 * (n / 2 - mx[i]) - cnt[i] << endl;
      ret = min(ret, mx[k - i] + 2 * (n / 2 - mx[k - i]) - cnt[i]);
    }
    return ret;
  }
};
