class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int mx = *max_element(nums.begin(), nums.end());
        vector<int> d(mx+2, 0);
        auto add = [&](int l, int r, int v) {
            d[l]+=v;
            d[r+1]-=v;
        };
        for (int i=0,j=n-1; i<j; i++, j--) {
            int a = max(nums[i], nums[j]), b = min(nums[i], nums[j]);
            int p = max(a, mx-b);
            add(0, p, 1);
            add(a-b, a-b, -1);
            add(p+1, mx, 2);
        }
        int ps = 0;
        int ans = n;
        for (int i=0; i<=mx; i++) {
            ps += d[i];
            ans = min(ans, ps);
        }
        return ans;
    }
};
