public class Solution {
    public int MinChanges(int[] nums, int k) {
        var count = new Dictionary<int, int>();
        var n = nums.Length;
        for(var i = 0; i < n / 2; i++)
        {
            var x = (int)Math.Abs(nums[i] - nums[n-i-1]);
            var c = count.GetValueOrDefault(x) + 1;
            count[x] = c;
        }
        var ans = int.MaxValue;
        foreach(var key in count.Keys.OrderByDescending(x => count[x]))
        {
            var t = Find(key);
            if(t >= ans)
            {
                return ans;
            }
            ans = t;
        }
        return ans;
        int Find(int maxNumber)
        {
            var res = 0;
            for(var i = 0; i < n / 2; i++)
            {
                var x = (int)Math.Abs(nums[i] - nums[n-i-1]);
                if(maxNumber == x) continue;

                if(Abs(Math.Min(nums[i], nums[n-i-1]), k) >= maxNumber || Abs(Math.Max(nums[i], nums[n-i-1]), 0) >= maxNumber)
                {
                    //Console.WriteLine($"+1: {nums[i]}, {nums[n-i-1]}");
                    res++;
                }
                else
                {
                    //Console.WriteLine($"+2: {nums[i]}, {nums[n-i-1]}");
                    res += 2;
                }
            }
            return res;
        }
    }



    int Abs(int x, int y)
    {
        return (int)Math.Abs(x - y);
    }
}
