class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<long long> d(k + 3);
        auto add([&](int l, int r, int x) {
            l ++, r ++;
            d[l] += x;
            d[r + 1] -= x;
        });
        for(int i = 0, j = n - 1; i < j; i ++, j --) {
            int x = nums[i], y = nums[j];
            if(x > y) swap(x, y);
            int p = y - x;
            add(0, p - 1, 1);
            int maxw = max(x, k - y);
            add(p + 1, p + maxw, 1);
            if(p + maxw + 1 <= k) add(p + maxw + 1, k, 2);
        }
        for(int i = 1; i <= k + 1; i ++) d[i] += d[i - 1];
        long long res = 1e9;
        for(int i = 1; i <= k + 1; i ++) res = min(res, d[i]);
        return res;
    }
};
