#include <ext/pb_ds/assoc_container.hpp>
using namespace __gnu_pbds;
template <class T>
using Tree = tree<T, null_type, less_equal<T>, rb_tree_tag, tree_order_statistics_node_update>;
// tree

class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        Tree<int> tree;
        int n = nums.size();
        map<int,vector<int>> mp;
        for (int i = 0; i < n / 2; i++){
            if (nums[i] > nums[n - 1 - i]){
                swap(nums[i], nums[n - 1 - i]);
            }
            mp[nums[n - 1 - i] - nums[i]].push_back(max(nums[n - 1 - i], k - nums[i]));
        }
        int ans = 1e9;
        int cnt = n / 2;
        for (auto it: mp){
            ans = min((unsigned long long)ans, (long long)cnt - it.second.size() + tree.order_of_key(it.first));
            for (auto i: it.second){ 
                tree.insert(i);
            }
        }
        return ans;
    }
};
