class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        counter = [0] * (k+1)
        counter_min = [0] * (k+1)
        for i in range(len(nums) // 2):
            diff = abs(nums[i] - nums[-i-1])
            min_v = max(k - min(nums[i], nums[-i-1]), max(nums[i], nums[-i-1]))
            counter_min[min_v] += 1
            counter[diff] += 1
        adder = 0
        ans = len(nums)
        for i in range(k+1):
            if adder >= ans:
                break
            ans_i = (len(nums) // 2 + adder - counter[i])
            if ans_i < ans:
                ans = ans_i
            adder += counter_min[i]
        return ans
