class Solution {
public:
    // 1, 2
    // 0, 4
    // 1, 1
    int minChanges(vector<int>& a, int k) {
        vector<int> v1;
        int n = a.size();
        map<int,int> already;
        for(int i=0;i<n/2;i++){
            int x = min(a[i],a[n-1-i]);
            int y = max(a[i],a[n-1-i]);
            int maxdiff = max({y,k-y,x,k-x});
            v1.push_back(maxdiff);
            already[y-x]++;
        }
        sort(v1.begin(),v1.end());
        int ans = 1e9;
        int pt = 0;
        for(int i=0;i<=k;i++){

            int curr = pt*2 + (n/2-pt)*1 - already[i];
            while(pt<n/2 && i==v1[pt]){
                pt++;
            }
            ans = min(curr,ans);
        }
        return ans;


    }
};
