class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int, int> f;
        vector<int> Min(k+5);

        int n = nums.size();
        for (int i = 0; i < n/2; i++) {
            int aux = abs(nums[i] - nums[n-i-1]);
            f[aux]++;

            int aux2 =  max( max(k-nums[i], k-nums[n-i-1]), max(nums[i], nums[n-i-1]) );
            Min[ aux2 ]++;
        }

        for (int i = 1; i <= k; i++) {
            Min[i] += Min[i-1];
        }

        int ans = INT_MAX;
        for (int target = 0; target <= k; target++) {
            int aux;
            if (target != 0)
                aux = n/2 - f[target] + Min[target-1];
            else
                aux = n/2 - f[target];
            ans = min(ans, aux);

            //cout << target << " " << aux <<  "\n";
        }

        return ans;
    }
};
