class Solution {
public:
using ll = long long;
    int minChanges(vector<int>& nums, int k) {
        int n = (int)nums.size();
        vector<int> f(k + 1, 0);
        vector<ll> p(k + 1, 0);
        for (int i = 0; i < n / 2; i++) {
            f[abs(nums[i] - nums[n - 1 - i])]++;
            int mx = max(abs(nums[i] - 0), abs(nums[i] - k));
            mx = max({mx, abs(nums[n - 1 - i] - 0), abs(nums[n - 1 - i] - k)});
            p[mx]++;
        }

        int leq_one = n / 2;
        ll ans = n + 1;
        for (int i = 0; i <= k; i++) {
            ll zero = f[i];

            ll h = (leq_one - zero) + (n / 2 - leq_one) * 2LL;
            ans = min(ans, h);

            leq_one -= p[i];
        }

        return ans;
    }
};
