class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        vector<int>v;
        vector<pair<int,int>>vp(n);
        map<int,int>mp;
        for(int i=0;i<n/2;i++){
            int u=nums[i],v2=nums[n-i-1];
            if(u>v2){
                swap(u,v2);
            }
            mp[(v2-u)]++;
            v.push_back(max((k-u),(v2)));
        }
        sort(v.begin(),v.end());
        int res=1e9;
        int cnt=0;
        int len=v.size();
        int ii=0;
        for(int i=0;i<=k;i++){
            res=min(res,(cnt+((n/2)-(mp[i]))));
            while((ii<len) && (i>=v[ii])){
                ii++; cnt++;
            }
        }
        return res;
    }
};
