class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n = a.size();
        vector<int> F(k + 1), G(k + 1);
        for (int i = 0; i < n / 2; ++i)
        {
            int x = abs(a[i] - a[n - i - 1]);
            F[x] += 1;
            int w = max(k - min(a[i], a[n - i - 1]), max(a[i], a[n - i - 1]));
            G[w] += 1;
        }

        int res = n / 2;
        int bad = 0;
        for (int i = 0; i <= k; ++i)
        {
            res = min(res, n / 2 - F[i] + bad);
            bad += G[i];
        }

        return res;
    }
};
