class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int>poss;
        int i=0;
        int j=nums.size()-1;
        int total=nums.size()/2;
        vector<int>m(k+2,0);
        vector<int>here(k+1,0);
        while(i<=j){
            int val1= abs(nums[i]-0);
            int val2=abs(nums[i]-k);
            int val3=abs(nums[j]-k);
            int val4=abs(nums[j]-0);
            val4=max({val1,val2,val3,val4});
            m[val4+1]++;
            here[abs(nums[i]-nums[j])]++;
            i++;
            j--;
        }
        for(int i=1;i<m.size();i++){
            m[i]+=m[i-1];
        }
        int ans=100000;
        for(int i=0;i<=k;i++){
            int has=here[i];
            int doub=m[i]; 
            int left=total-has-doub;
            doub=doub*2;
          //  cout<<i<<" "<<has<<" "<<doub<<" "<<left<<" "<<endl;
            has=0;
            has+=left;
            has+=doub;
            ans=min(ans,has);

        }
        return ans;


    }
};
