const int N = 2e5 + 7;//序列长度
const int M = N << 2;//线段树4 * n空间
int a[N];
struct SegT {
	int l, r;
	long long sum;
	long long flag;
}tr[M];
void pushup(SegT& u, SegT& l, SegT& r) {
	u.sum = l.sum + r.sum;
}
void pushdown(SegT& u, SegT& l, SegT& r) {
	if (u.flag)
	{
		int mid = u.l + u.r >> 1;
		l.sum += (mid - l.l + 1) * u.flag; r.sum += (r.r - mid) * u.flag;
		l.flag += u.flag; r.flag += u.flag;
		u.flag = 0;
	}
}
inline void build(int u, int l, int r)
{
	tr[u] = { l,r,0,0 };
	if (l == r) return;
	int mid = l + r >> 1;
	build(u << 1, l, mid); build(u << 1 | 1, mid + 1, r);
	pushup(tr[u], tr[u << 1], tr[u << 1 | 1]);
}
inline void modify(int u, int l, int r, int c)
{
	if (tr[u].l >= l && tr[u].r <= r)
	{
		tr[u].sum += (tr[u].r - tr[u].l + 1) * c;
		tr[u].flag += c;
		return;
	}
	int mid = tr[u].l + tr[u].r >> 1;
	pushdown(tr[u], tr[u << 1], tr[u << 1 | 1]);
	if (l <= mid) modify(u << 1, l, r, c);
	if (r > mid) modify(u << 1 | 1, l, r, c);
	pushup(tr[u], tr[u << 1], tr[u << 1 | 1]);
}
inline long long query(int u, int l, int r)
{
	if (tr[u].l >= l && tr[u].r <= r) return tr[u].sum;
	int mid = tr[u].l + tr[u].r >> 1;
	pushdown(tr[u], tr[u << 1], tr[u << 1 | 1]);
	long long tmp = 0;
	if (l <= mid)tmp += query(u << 1, l, r);
	if (r > mid) tmp += query(u << 1 | 1, l, r);
	return tmp;
}
class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> v(n / 2, 0);
        build(1, 1, k + 10);
        long long ans = 1e9;
        for (int i = 0; i < n / 2 ; i ++ ) {
            v[i] = max({nums[i], nums[n - i - 1], k - nums[i], k - nums[n - i - 1]});
            modify(1, 1, k + 5, 2);
            //[1,k+1]
            int t = abs(nums[i] - nums[n - i - 1]);
            modify(1, 1, v[i] + 1, -1);
            modify(1, t + 1, t + 1, -1);
        }
        for (int i = 1; i <= k + 1; i ++ ) ans = min(ans, query(1, i, i));
        return ans;
    }
};
