package main

import (
	"fmt"
	"sort"
)

func minChanges(nums []int, k int) int {
	m := make(map[int][]int)
	v := []int{}

	for i := 0; i < len(nums)/2; i++ {
		a := nums[i]
		b := nums[len(nums)-i-1]
		diff := abs(a - b)
		X := max(a, b, k-a, k-b)
		m[diff] = append(m[diff], X)
		v = append(v, X)
	}

	ans := len(nums)
	sort.Ints(v)

	for diff, values := range m {
		T := lowerBound(v, diff)
		T = T*2 + (len(nums)/2 - T)

		for _, X := range values {
			if X < diff {
				T -= 2
			} else {
				T -= 1
			}
		}

		if T < ans {
			ans = T
		}
	}

	return ans
}

func abs(x int) int {
	if x < 0 {
		return -x
	}
	return x
}

func max(values ...int) int {
	m := values[0]
	for _, v := range values {
		if v > m {
			m = v
		}
	}
	return m
}

func lowerBound(arr []int, target int) int {
	l, r := 0, len(arr)
	for l < r {
		mid := (l + r) / 2
		if arr[mid] < target {
			l = mid + 1
		} else {
			r = mid
		}
	}
	return l
}
