impl Solution {
    pub fn min_changes(nums: Vec<i32>, k: i32) -> i32 {
        let mut max_diffs = vec![0i32; k as usize + 1];
        let n = nums.len();
        let mut diff_count = vec![0i32; k as usize + 1];
        for i in 0..n / 2 {
            diff_count[(nums[i] - nums[n - i - 1]).abs() as usize] += 1;
            let max_diff =
                i32::max(k - nums[i], nums[i]).max(i32::max(k - nums[n - i - 1], nums[n - i - 1]));
            max_diffs[max_diff as usize] += 1;
        }

        let mut ans = (n / 2) as i32 - diff_count[0];

        let mut acc = 0;
        for i in 1..=k as usize {
            let curr_ans = (n / 2) as i32 - diff_count[i];
            acc += max_diffs[i - 1];
            ans = ans.min(curr_ans + acc);
        }
        ans
    }
}
