class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,int> cnt;
        vector<int> v(100001);
        int n = nums.size();

        // for(int i = 0; i <= 10;++i)
        //     cout<<i<<" ";
        // cout<<endl;

        for(int i = 0; i < nums.size() / 2; ++i){
            int mn = min(nums[i], nums[n-i-1]);
            int mx = max(nums[i], nums[n-i-1]);

            int diff = mx - mn;

            int upper = max({abs(k-mn), mx});
            // printf("%d %d %d %d\n",mn,mx,diff,upper);
            ++v[0];
            if(k >= upper+1){
                ++v[upper + 1];
                if(diff >= upper+1){
                    v[diff] -= 2;
                    v[diff+1] += 2;
                }else{
                    --v[diff];
                    ++v[diff+1];
                }
            }else{
                ++v[diff+1];
                --v[diff];
            }
            // int cur = 0;
            // for(int i = 0; i <= 10; ++i){
            //     cur += v[i];
            //     cout<<cur<<" ";
            // }
            // cout<<endl;


        }
        int ans = INT_MAX;
        int cur = 0;
        for(auto i : v){
            cur += i;
            ans = min(ans, cur);
            // cout<<cur<<" ";
        }
        return ans;
    }
};
