class Solution {
public:
    int abs(int k){return k>0?k:-1*k;}

    int check(vector<int>&v,int z,int k){
        int n=v.size(),ans=0;
        for(int i=0;i<n/2;i++){
            int a=v[i],b=v[n-1-i];
            if(abs(a-b)==z){continue;}
            if(a+z<=k||a-z>=0||b+z<=k||b-z>=0)
            {ans++;}
            else{ans+=2;}
        }
        return ans;
    }

    int minChanges(vector<int>& v, int k) {
        map<int,int> mp;
        int n=v.size();
        for(int i=0;i<n/2;i++){
            mp[abs(v[i]-v[n-1-i])]++;
        }
        vector<pair<int,int>> vp;
        for(auto x:mp){
            vp.push_back({x.second,x.first});
        }
        sort(vp.rbegin(),vp.rend());
        int ans=n/2;
        for(auto x:vp){
            int p=n/2-x.first;
            if(ans<=p){return ans;}
            p=check(v,x.second,k);
            ans=min(ans,p);
        }
        return ans;

    }
};
