class Solution {
public:
    int minChanges(vector<int>& nums, int k) 
    {
        int n=nums.size();
        int ans=INT_MAX;;
        map<int,int>mp;
        int r=n-1;
        int mx=0;
        int mxv=0;
        for(int i=0;i<n/2;i++)
        {
            mp[abs(nums[i]-nums[r])]++;
            if(mx<mp[abs(nums[i]-nums[r])]){mx=mp[abs(nums[i]-nums[r])];mxv=abs(nums[i]-nums[r]);}
            r--;
        }
        r=n-1;
        vector<int>sg(n/2,0);
        for(int i=0;i<n/2;i++)
        {
            sg[i]=max(max(nums[i],nums[r]),k-max(nums[i],nums[r]));
            sg[i]=max(sg[i],nums[i]);
            sg[i]=max(sg[i],nums[r]);
            sg[i]=max(sg[i],k-nums[i]);
            sg[i]=max(sg[i],k-nums[r]);
            // cout<<sg[i]<<" ";
            r--;
        }
        // cout<<endl;
        sort(sg.begin(),sg.end());
        // for(int i=0;i<=k;i++)
        for(auto it:mp)
        {
            int fv=it.first;
            // int fv=i;
            int sv=it.second;
            // int sv=mp[i];
            int mul2=lower_bound(sg.begin(),sg.end(),fv)-sg.begin();
            // cout<<mul2<<" "<<fv<<endl;
            // int mul2=n/2-ind;
            int c=2*mul2+(n/2-mul2-sv);
            // cout<<fv<<" "<<sv<<" "<<c<<endl;
            ans=min(ans,c);
        }
        // cout<<mx<<" "<<mxv<<endl;
        return ans;


    }
};
