class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        std::unordered_map<int, int> freq;
        vector<int> range;
        // 记录所有 (i, n-i-1) 的初始绝对差值
        for (int i = 0; i < n / 2; ++i) {
            int diff = abs(nums[i] - nums[n - i - 1]);
            int a = nums[i];
            int b = max(nums[i],k - nums[i]);
            int c = max(b, nums[n-i-1]);
            int d = max(c, k - nums[n-i-1]);
            range.push_back(d);
            freq[diff]++;
        }
        vector<pair<int,int>> qu;
        sort(range.begin(), range.end());
        for (const auto& pair : freq) {
            qu.push_back({pair.second,pair.first});
        }
        sort(qu.begin(),qu.end());
        int ans = INT_MAX;
        for (int i = qu.size() - 1; i >= 0; i--) {
            int diff = qu[i].second;
            int index = std::lower_bound(range.begin(), range.end(), diff)-range.begin();
            ans = min(ans,n/2-qu[i].first+index);
        }
        return ans;
    }
};
