class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> counts(k + 1), pSum(k + 1);
        const int n = nums.size();
        for (int i = 0, j = n - 1; i < j; ++i, --j) {
            const int x = min(nums[i], nums[j]), y = max(nums[i], nums[j]);
            ++counts[y - x];
            ++pSum[max(y, k - x)];
        }
        for (int i = k - 1; i >= 0; --i) {
            pSum[i] += pSum[i + 1];
        }

        int ans = INT_MAX;
        for (int i = 0; i <= k; ++i) {
            ans = min(ans, n - counts[i] - pSum[i]);
        }
        return ans;
    }
};
