class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        map<int,int> mp;
        map<int,int> hs;
        int n=a.size();
        for(int i=0;i<n/2;i++)
        {
            int diff=abs(a[i]-a[n-i-1]);
            int val=max(max(a[i],k-a[i]),max(a[n-i-1],k-a[n-i-1]));
            hs[val]++;
            mp[diff]++;
        }
        vector<pair<int,int>> vec;
        for(auto x:mp)
        vec.push_back({x.first,0});
        for(auto x:hs)
        vec.push_back({x.first,1});
        sort(vec.begin(),vec.end());
        int ans=n/2;
        int x=n/2,y=0;
        for(int i=0;i<vec.size();i++)
        {
            // cout<<vec[i].first<<" "<<vec[i].second<<endl;
            if(vec[i].second==0)
            {
                // cout<<vec[i].first<<" "<<mp[vec[i].first]<<" "<<y<<endl;
                ans=min(ans,y*2+x-mp[vec[i].first]);
            }
            else
            {
                x-=hs[vec[i].first];
                y+=hs[vec[i].first];
            }
        }
        return ans;
    }
};
