int c[100005]{};
template<typename T>
void chkMin(T& a, T b)
{
    if(a>b)a = b;
}
class Solution {
public:

    int minChanges(vector<int>& a, int k) {
        int n = a.size();
        int d = 0, ans = 2e9;
        for (int i = 0; i < n - 1 - i; i++) {
        	int v = abs(a[i] - a[n - 1 - i]);
        	int x = a[i], y = a[ n - 1 - i];
        	int U = max(max(x, y), max(k - x, k - y));
        	c[U+1]++;
        	c[v+1]++;
        	c[0]++;
        	c[v]--;
        	d++;
        }
	    for (int i = 0; i <= k; i++) {
	    	if(i)c[i] += c[i - 1];
	    	chkMin(ans, c[i]);
	    }
	    for (int i = 0; i <= k+1; i++) c[i] = 0;
        return ans;
    }
};
