class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size(), cur = 0, res = n * 2, m = n / 2;
        vector<int> cnt(k + 1, 0);
        vector<int> duo(k + 1, 0);
        for(int i = 0, j = n - 1; i < j; i++, j--) {
            if(nums[i] > nums[j]) swap(nums[i], nums[j]);
            cnt[nums[j] - nums[i]]++;
            duo[max(k - nums[i], nums[j])]++;
        }
        for(int i = 0; i <= k; i++) {
            //cout<<i<<' '<<cnt[i]<<' '<<cur<<endl;
            res = min(res, m - cnt[i] + cur);
            cur += duo[i];
        }
        return res;
    }
};
