func minChanges(nums []int, k int) int {
	n := len(nums)
	arr := make([]int, n)
	temp := []int{}
	m := map[int]int{}

	for i := 0; i < n-i-1; i++ {
		arr[i] = nums[i]
		arr[i+1] = nums[n-i-1]

		t1 := max(arr[i], arr[i+1])
		t2 := max(k-arr[i], k-arr[i+1])
		temp = append(temp, max(t1, t2))

        m[abs(arr[i+1]-arr[i])]++
	}

	sort.Ints(temp)
	res := n
	for k, v := range m {
		// 把所有相邻元素调成k
		// 找到temp中第一个大于等于k的下标
		l, r := 0, len(temp)-1
		for l < r {
			mid := (l + r) >> 1
			if temp[mid] >= k {
				r = mid
			} else {
				l = mid + 1
			}
		}

		left := l
		right := len(temp) - l - v 
		cnt := left*2 + right
		res = min(res, cnt)
	}
	return res
}

func abs(x int) int {
	if x < 0 {
		return -x 
	}
	return x 
}
