class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,int> diff;
        int n=nums.size();
        for(int i=0; i<n/2;i++)
        {
            diff[abs(nums[i]-nums[n-1-i])]++;
        }
        vector<pair<int,int>> pq;
        for(auto it:diff)
        {
            pq.push_back({it.second,it.first});
        }
        sort(pq.begin(),pq.end(),[&](pair<int,int> &a,pair<int,int> &b){
            if(a.first==b.first) return a.second<b.second;
            return a.first>b.first;
        });
        int res=INT_MAX;
        for(int i=0; i<pq.size();i++)
        {
            int dif=pq[i].second;
            int same=pq[i].first;
            if(dif>k)
            {
                continue;
            }
            if(n/2-same>res) break;
            int ans=0;
            int x=dif;
            for(int i=0; i<n/2;i++)
            {
                if(nums[i]<nums[n-1-i]) swap(nums[i],nums[n-1-i]);
                if(nums[i]-nums[n-1-i]==x) continue;
                if(nums[n-1-i]+x<=k) ans++;
                else if(nums[i]-x>=0) ans++;
                else    ans+=2;
            }
            res=min(ans,res);
        }

        return res==INT_MAX? 0:res;
    }
};
