class Solution {
public:
    int minChanges(vector<int>& arr, int k) {
        map<int,int> mp,mp1;
        int n=arr.size();
        int l=0,hi=n-1,ind=0,minn=INT_MAX;
        vector<int> v(k+1);
        while(l<=hi)
        {
            v[abs(arr[l]-arr[hi])]-=2;
            if(abs(arr[l]-arr[hi])+1<=k)
            v[abs(arr[l]-arr[hi])+1]+=2;
            if(max(max(arr[l],arr[hi])-0,k-min(arr[hi],arr[l]))+1<=k)
                v[max(max(arr[l],arr[hi])-0,k-min(arr[hi],arr[l]))+1]+=2;     
            l++;
            hi--; 
        }
        int sum=0;
        for(int i=0;i<=k;i++)
        {

            sum+=v[i];
            if(minn>sum)
            {
                minn=sum;
                ind=i;
            }
        }

      //  cout<<ind<<endl;
        l=0,hi=n-1;
        int ans=0;
        while(l<=hi)
        {
            if(abs(arr[l]-arr[hi])==ind);
            else if(max(max(arr[l],arr[hi])-0,k-min(arr[hi],arr[l]))>=ind)ans++;
            else ans+=2;
            l++;
            hi--;
        }
        return ans;
    }
};
