class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        d = defaultdict(int)
        n = len(nums)
        for i in range(n//2):
            d[abs(nums[i]-nums[n-i-1])] += 1

        arr = [(-v, k) for k, v in d.items()]
        arr.sort()

        ans = 0
        for i in range(n//2):
            t = abs(nums[i]-nums[n-i-1])
            if t == arr[0][1]: continue
            if min(nums[i], nums[n-i-1]) > k-arr[0][1] and max(nums[i], nums[n-i-1]) < arr[0][1]:
                ans += 2
            else:
                ans += 1

        ans2 = inf
        if len(arr) > 1 and arr[0][0] == arr[1][0]-1:
            ans2 = 0
            for i in range(n//2):
                t = abs(nums[i]-nums[n-i-1])
                if t == arr[1][1]: continue
                if min(nums[i], nums[n-i-1]) > k-arr[1][1] and max(nums[i], nums[n-i-1]) < arr[1][1]:
                    ans2 += 2
                else:
                    ans2 += 1
        return min(ans, ans2)
