class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int, int> map;

        int n = nums.size();

        for(int i = 0; i < n/2; i++){
            map[abs(nums[i] - nums[n-i-1])]++;
        }

        vector<pair<int, int>> v;
        for(auto it:map){
            v.push_back({it.second, it.first});
        }
        sort(v.rbegin(), v.rend());

        int ans = 100000;
        for(int i = 0; i < v.size(); i++){
            int cnt = 0, diff = v[i].second;

            for(int ind = 0; ind < n/2; ind++){
                int x = nums[ind], y = nums[n - ind - 1];

                if(abs(x-y) == diff) continue;
                else if(x + diff > k && x - diff < 0 && y + diff > k && y - diff < 0) cnt += 2;
                else cnt++;
            }

            if(cnt > ans) return ans;
            else ans = cnt;
        }

        return ans;
    }
};
