class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        map<int,int> m;
        int n = a.size();
        for(int i=0; i<n/2;++i){
            m[abs(a[i]-a[n-i-1])]++;
        }
        // int mx = 0;
        // int lol;
        // for(auto &i : m){
        //     if(i.second>mx){
        //         mx = i.second;
        //         lol = i.first;
        //     }
        // }
        vector<int> v;
        for(int i=0; i<n/2;++i){
            // if(abs(a[i]-a[n-i-1])==lol) continue;
            // if((lol<=abs(min(a[i],a[n-i-1])-k)) || (lol<=abs(max(a[i],a[n-i-1])))){
            //     ans++;
            // }
            int one = abs(min(a[i],a[n-i-1])-k);
            int two = abs(max(a[i],a[n-i-1]));
            v.push_back(max(one,two));
            // else {
            //     ans+=2;
            // }
        }
        sort(v.begin(),v.end());
        int ans = INT_MAX;
        for(int i=0; i<=k;++i){
            int ind = lower_bound(v.begin(),v.end(),i) - v.begin();
            int lol = v.size()-ind;
            lol-=m[i];
            int ff = lol + (ind*2);
            ans = min(ans,ff);
        }
        return ans;
    }
};
