class Solution {
public:
    int minChanges(vector<int>& nums, int k) 
    {
        map<int,int,std::less<int>> diffs;
        vector<int> maxDiff(100001,0);
        int sec = nums.size()-1;
        for(int i =0;i<nums.size()/2;i++)
        {
            int minn = min(nums[i],nums[sec]);
            int maxx = max(nums[i],nums[sec]);
            diffs[maxx-minn]++;
            maxDiff[max(k-minn,maxx)]++;
            sec--;
        }
        int minCh = INT_MAX;
        int j = 0;
        int maxDiffC = nums.size()/2;
        for(auto p : diffs)
        {
           // cout<<p.first<<" "<<p.second<<"\n";
            while(p.first>j&&j<=k)
            {
                maxDiffC-=maxDiff[j];
                j++;
            }
            //cout<<maxDiffC<<"\n";
            minCh = min((int)nums.size()-maxDiffC-p.second,minCh);
        }
        //cout<<"\n";
        return minCh;
    }
};
