class Solution {
public:
    int minChanges(vector<int>& A, int k) {
        map<int,int>mp;
        vector<int>cnt;
        int n = A.size();
        for(int i=0;i<A.size()/2;i++){
            mp[abs(A[i]-A[A.size()-1-i])]++;
            cnt.push_back(max({A[i],k-A[i],A[n-1-i],k-A[n-1-i]}));
        }
        int ans = 1e9;
        sort(cnt.begin(),cnt.end());
        for(auto i:mp){
            int x = lower_bound(cnt.begin(),cnt.end(),i.first)-cnt.begin();
            ans = min(ans,n/2-i.second+x);
        }
        return ans;
    }
};
