class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,int>mp;
        int n = nums.size();
        for(int i=0;i<n;i++){
            int d= abs(nums[i]-nums[n-i-1]);
            mp[d]++;
        }
        vector<pair<int,int>>vp;
        for(auto&it:mp)vp.push_back({it.second,it.first});
        sort(vp.begin(),vp.end(),[](pair<int,int>&a,pair<int,int>&b)->bool{
            if(a.first==b.first)return a.second<b.second;
            return a.first>b.first;
        }); 


        int out = 1e7;
        int lp = min(12,(int)vp.size());
        for(int j=0;j<lp;j++){
            int count = 0; 
            int mod = vp[j].second; 
            for(int i=0;i<n/2;i++){
                int d= abs(nums[i]-nums[n-i-1]);
                if(d!=mod){
                    if(mod<=k/2){
                        count++;
                    }
                    else{
                        int lm = min(nums[i],nums[n-i-1]);
                        int rm = max(nums[i],nums[n-i-1]);
                        if(lm<=(k-mod)||rm>=mod)count++;
                        else count+=2;
                    }
                }
            }  
            out = min(out,count);
        }
        int x = (n/2) - mp[0]/2;
        return min(x,out);
    }
};
