class Solution {
public:

    int dispar[200005], dist[200005];
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();

        for(int i = 0; i < n/2; i++)
            dist[abs(nums[i]-nums[n-1-i])]++;
        for(int i = 0; i < n/2; i++)
            dispar[abs(nums[i]-nums[n-1-i])+max(k-max(nums[i], nums[n-1-i]), min(nums[i], nums[n-1-i]))+1]++;

        int mini = 1<<30, cnt = 0, dobles = 0;       
        for(int i = 0; i < 200005; i++)
        {
            dobles += dispar[i];
            if(dist[i] == 0)
                continue;
            cnt = dobles*2 + ((n/2)-dobles-dist[i]);
            mini = min(mini, cnt);
        }
        return mini;
    }
};
