using PII = pair<int,int>;
class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map<int,int> cnt; // diff, cnt
        for (int i=0; n-i-1>i; i++) {
            int diff = abs(nums[i] - nums[n-i-1]);
            if (diff <= k) {
                cnt[diff] += 1;
            }
        }
        vector<PII> arr;
        for (auto[diff, c]: cnt) {
            arr.push_back({c, diff});
        }
        sort(arr.rbegin(), arr.rend());
        int ans = INT_MAX;
        for (int i=0; i<arr.size() && (i+1)*(n) < int(1e7); i++) {
            ans = min(ans, helper(nums, k, arr[i].second));
        }
        return ans;

    }
    int helper(vector<int>& nums, int k, int DIFF) {
        int n = nums.size();
        int ans = 0;
        for (int i=0; n-i-1>i; i++) {
            int diff = abs(nums[i] - nums[n-i-1]);
            if (diff != DIFF) {
                int a = min(nums[i], nums[n-i-1]);
                int b = max(nums[i], nums[n-i-1]);
                if (a + DIFF <= k) {
                    ans +=1;
                    continue;
                } else if (b - DIFF >= 0) {
                    ans += 1;
                    continue;
                } else {
                    ans += 2;
                    continue;
                }
            }
        }
        return ans;
    }
};
