class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int> mp;
        int n = nums.size(),t;
        for(int i=0; i<(n/2); ++i){
            t = abs(nums[i] - nums[n-i-1]);
            mp[t]++;
        }
        int maxc = INT_MIN, mx;
        for(auto it: mp){
            if(it.second > maxc){
                maxc = it.second;
                mx = it.first;
            }
        }
        mp.erase(mx);
        int smaxc = INT_MIN, smx;
        for(auto it: mp){
            if(it.second > smaxc){
                smaxc = it.second;
                smx = it.first;
            }
        }
        int cnt=0,mnt,mxt;
        for(int i=0; i<(n/2); ++i){
            t = abs(nums[i] - nums[n-i-1]);
            if(t != mx){
                mnt = min(nums[i], nums[n-i-1]);
                mxt = max(nums[i],nums[n-i-1]);
                if(mxt >= mx || (k-mnt) >= mx) cnt++;
                else cnt+=2;
            }
        }
        int cnt2=0;
        for(int i=0; i<(n/2); ++i){
            t = abs(nums[i] - nums[n-i-1]);
            if(t != smx){
                mnt = min(nums[i], nums[n-i-1]);
                mxt = max(nums[i],nums[n-i-1]);
                if(mxt >= smx || (k-mnt) >= smx) cnt2++;
                else cnt2+=2;
            }
        }
        return min(cnt,cnt2);
    }
};
