class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        unordered_map<int, int> mp;

        int n = nums.size();
        for(int i=0; i<n/2; i++)
        mp[abs(nums[i]-nums[n-1-i])]++;

        vector<int> tars;
        for(auto& i: mp)
        tars.push_back(i.first);

        sort(tars.begin(), tars.end(), [&](int a, int b){
            return mp[a]>mp[b];
        }); 

        for(int i: tars) cout<<i<<" ";

        int ans = n - mp[tars[0]]*2;

        for(int tar: tars){
            int curans = 0;
            int minn = (n - mp[tar]*2)/2;
            if(minn>=ans) break;
            for(int l=0, r=n-1; l<r; l++, r--){
                int dif = abs(nums[l]-nums[r]);
                if(dif==tar) continue;
                if(nums[l]+tar<=k or nums[l]-tar>=0) curans++;
                else if(nums[r]+tar<=k or nums[r]-tar>=0) curans++;
                else curans+=2;
            }
            // cout<<"at tar = "<<tar<<" ans = "<<curans<<endl;
            ans = min(ans, curans);
        }
        return ans;
    }
};
